package com.acc_hunter_web.forgotpasswordservice.controller;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

import com.acc_hunter_web.forgotpasswordservice.ForgotPasswordService;
import com.wavemaker.tools.api.core.annotations.WMAccessVisibility;
import com.wavemaker.tools.api.core.models.AccessSpecifier;
import com.wordnik.swagger.annotations.Api;

/**
 * Controller object for domain model class {@link ForgotPasswordService}.
 * @see ForgotPasswordService
 */
@RestController
@Api(value = "ForgotPasswordController", description = "controller class for java service execution")
@RequestMapping("/forgotPassword")
public class ForgotPasswordController {

    @Autowired
    private ForgotPasswordService forgotPasswordService;

    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    @RequestMapping(value = "/password", method = RequestMethod.GET)
    public String getPassword() {
        return forgotPasswordService.getPassword();
    }

    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    @RequestMapping(value = "/sendEmail", method = RequestMethod.GET)
    public String sendEmail(@RequestParam(value = "toEmailAddress", required = false) String toEmailAddress) {
        return forgotPasswordService.sendEmail(toEmailAddress);
    }
}